import { Component, EventEmitter, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, ValidationErrors, Validators } from '@angular/forms';
import {Observable, Observer} from 'rxjs';
import {NzMessageService} from 'ng-zorro-antd/message';
import {ActivatedRoute, Route, Router} from '@angular/router';
import {Product} from '../../../interfaces';
import {ProductService} from '../../../services/product.service';

@Component({
  selector: 'app-welcome',
  templateUrl: './products-view.component.html',
  styleUrls: ['./products-view.component.scss']
})
export class ProductsViewComponent implements OnInit {

  updateList = new EventEmitter();

  public productId: number = 0;
  public product: any;

  private urlReg = '(https?://)?([\\da-z.-]+)\\.([a-z.]{2,6})[/\\w .-]*/?';
  previewImg = 'data:image/png;base64,';
  fallback = 'data:image/png;base64,';

  formatterDollar = (value: number) => `$ ${value}`;
  parserDollar = (value: string) => value.replace('$ ', '');

  public validateForm: FormGroup = this.fb.group({
    id: [''],
    name: ['', [Validators.required, Validators.maxLength(30)]],
    description: ['', [Validators.maxLength(200)]],
    img: ['', [Validators.pattern(this.urlReg)]],
    price: ['', [Validators.required]],
  });

  constructor(
    private msg: NzMessageService,
    private router: Router,
    private activatedRoute: ActivatedRoute,
    private productService: ProductService,
    private fb: FormBuilder
  ) {
  }

  ngOnInit() {
    this.activatedRoute.params.subscribe((params: any) => {
      if (params['id']) {
        this.productId = +params['id'];
        this.productService.getProduct(this.productId)
          .subscribe((resp: any) => {
            this.product = resp;
            this.validateForm.patchValue({...this.product});
            this.previewImg = this.product.img;
          }, error => {
            this.msg.error(error.message || 'Invalid ID');
            return this.router.navigate(['products/add']);
          });
      }
    });
    this.validateForm.get('img')?.valueChanges.subscribe(x => {
      if (this.validateForm.get('img')?.valid) {
        this.previewImg = x;
      }
    });
  }

  submitForm(): void {
    if (!this.validateForm.valid) {
      this.msg.error('The form is invalid!');
      return;
    }
    const data = this.validateForm.value;

    this.productService.saveProduct(data)
      .subscribe((resp: Product) => {
        this.product = resp;
        this.updateList.emit();
        this.msg.success('Product saved successfully!');
        if (!this.productId) {
          return this.router.navigate(['products/view/' + this.product.id]);
        } else {
          this.validateForm.patchValue({...this.product});
          this.previewImg = this.product.img;
        }
      }, error => {
        this.msg.error(error.message || 'Failed to save product');
      });
  }

  resetForm(e: MouseEvent): void {
    e.preventDefault();
    this.validateForm.reset();
    for (const key in this.validateForm.controls) {
      this.validateForm.controls[key].markAsPristine();
      this.validateForm.controls[key].updateValueAndValidity();
    }
    this.validateForm.get('id')?.setValue(this.productId);
  }


}
